from transformers import LogitsProcessor
class CustomLogitsProcessor(LogitsProcessor):
    def __init__(self, eos_token_id, para_begin_token_id, para_end_token_id, 
        title_start_token, title_end_token,sync_start_id,sync_end_id,
        first_time=True):
        self.eos_token_id = eos_token_id
        self.para_begin_token_id = para_begin_token_id
        self.para_end_token_id = para_end_token_id
        self.title_start_token = title_start_token
        self.title_end_token=title_end_token
        self.sync_start_id=sync_start_id
        self.sync_end_id=sync_end_id
        self.supress_tokens = [sync_end_id, para_begin_token_id, para_end_token_id]
        self.para_start = False
        self.title_stage = False
        self.first_time = first_time

    def __call__(self, input_ids: torch.LongTensor, scores: torch.FloatTensor) -> torch.FloatTensor:
        # return scores
        # è·å–å½“å‰ç”Ÿæˆçš„åºåˆ—é•¿åº¦
        seq_length = input_ids.shape[-1]
        min_value = float('-inf')

        # ä¸åº”è¯¥åœ¨ä¸²è¡Œè¿‡ç¨‹å‡ºç°çš„tokens
        scores[:, self.supress_tokens] =  min_value
        # ç¬¬äºŒæ¬¡ä¸²è¡Œä¸åº”è¯¥é©¬ä¸Šå¼€å§‹ç”Ÿæˆå¹¶è¡Œtoken
        if not self.first_time:
            scores[:, self.title_start_token] =  min_value
            self.first_time = True

        # è¿›å…¥äº†æ ‡é¢˜ç”Ÿæˆé˜¶æ®µ
        if input_ids[0, -1] == self.title_start_token:
            self.title_stage = True

        if self.title_stage:
            # å¦‚æœä¸Šä¸€ä¸ªtokenæ˜¯æ ‡é¢˜ç»“æŸtoken
            if input_ids[0, -1] == self.title_end_token:
                # é‚£ä¹ˆç°åœ¨, åªèƒ½æœ‰ä¸¤ä¸ªé€‰æ‹©: æ ‡é¢˜å¼€å§‹tokenå’Œå¹¶è¡Œå¼€å§‹token
                self.para_start = True
                title_start_val = scores[0, self.title_start_token].item()
                sync_start_val = scores[0, self.sync_start_id].item()
                # print(f'+++++###########title_start_val={title_start_val}, sync_start_val={sync_start_val}')
                scores[:, :] = min_value
                scores[:, self.title_start_token] = title_start_val
                scores[:, self.sync_start_id] = sync_start_val
            else:
                # å¦‚æœä¸Šä¸€ä¸ªtokenæ˜¯æ ‡é¢˜å¼€å§‹token
                if input_ids[0, -1] == self.title_start_token:
                    # é‚£ä¹ˆæ ‡é¢˜å¼€å§‹tokenã€æ ‡é¢˜ç»“æŸtokenå’Œå¹¶è¡Œå¼€å§‹tokenä¸åº”è¯¥å‡ºç°
                    scores[:, self.title_start_token] = min_value
                    scores[:, self.title_end_token] = min_value
                    scores[:, self.sync_start_id] = min_value
                    pass
                else:
                    # å¦‚æœæ˜¯æ­£å¸¸token,ä¸åº”è¯¥å‡ºç°æ ‡é¢˜å¼€å§‹tokenå’Œå¹¶è¡Œå¼€å§‹token
                    scores[:, self.title_start_token] = min_value
                    scores[:, self.sync_start_id] = min_value
        else:
            # é‚£ä¹ˆæ ‡é¢˜ç»“æŸtokenå’Œå¹¶è¡Œå¼€å§‹tokenä¸åº”è¯¥å‡ºç°
            scores[:, self.title_end_token] = min_value
            scores[:, self.sync_start_id] = min_value
        return scores


import time

class Qwen2ForParaCausalLM(Qwen2PreTrainedModel, GenerationMixin):
    _tied_weights_keys = ["lm_head.weight"]
    _tp_plan = {"lm_head": "colwise_rep"}
    _pp_plan = {"lm_head": (["hidden_states"], ["logits"])}

    def __init__(self, config):
        super().__init__(config)
        self.model = Qwen2ParaModel(config)
        self.vocab_size = config.vocab_size
        self.lm_head = nn.Linear(config.hidden_size, config.vocab_size, bias=False)

        # Initialize weights and apply final processing
        self.post_init()

    def get_input_embeddings(self):
        return self.model.embed_tokens

    def set_input_embeddings(self, value):
        self.model.embed_tokens = value

    def get_output_embeddings(self):
        return self.lm_head

    def set_output_embeddings(self, new_embeddings):
        self.lm_head = new_embeddings

    def set_decoder(self, decoder):
        self.model = decoder

    def get_decoder(self):
        return self.model

    @deprecate_kwarg("num_logits_to_keep", version="4.50", new_name="logits_to_keep")
    def forward(
        self,
        input_ids: torch.LongTensor = None,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Union[Cache, List[torch.FloatTensor]]] = None,
        inputs_embeds: Optional[torch.FloatTensor] = None,
        labels: Optional[torch.LongTensor] = None,
        use_cache: Optional[bool] = None,
        output_attentions: Optional[bool] = None,
        output_hidden_states: Optional[bool] = None,
        return_dict: Optional[bool] = None,
        cache_position: Optional[torch.LongTensor] = None,
        logits_to_keep: Union[int, torch.Tensor] = 0,
        para_begin_position=0,
        num_para=0,
        activate_branch: Optional[List[int]] = None,
        history_mask: Optional[dict] = None,
        **kwargs: Unpack[KwargsForCausalLM],
    ) -> Union[Tuple, CausalLMOutputWithPast]:
        output_attentions = output_attentions if output_attentions is not None else self.config.output_attentions
        output_hidden_states = (
            output_hidden_states if output_hidden_states is not None else self.config.output_hidden_states
        )
        return_dict = return_dict if return_dict is not None else self.config.use_return_dict

        # decoder outputs consists of (dec_features, layer_state, dec_hidden, dec_attn)
        outputs = self.model(
            input_ids=input_ids,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            inputs_embeds=inputs_embeds,
            use_cache=use_cache,
            output_attentions=output_attentions,
            output_hidden_states=output_hidden_states,
            return_dict=return_dict,
            cache_position=cache_position,
            num_para=num_para,
            activate_branch=activate_branch,
            history_mask=history_mask,
            para_begin_position=para_begin_position,
            **kwargs,
        )

        hidden_states = outputs[0]
        # Only compute necessary logits, and do not upcast them to float if we are not computing the loss
        slice_indices = slice(-logits_to_keep, None) if isinstance(logits_to_keep, int) else logits_to_keep
        logits = self.lm_head(hidden_states[:, slice_indices, :])

        loss = None
        if labels is not None:
            loss = self.loss_function(logits=logits, labels=labels, vocab_size=self.config.vocab_size, **kwargs)

        if not return_dict:
            output = (logits,) + outputs[1:]
            return (loss,) + output if loss is not None else output

        return ParaCausalLMOutputWithPast(
            loss=loss,
            logits=logits,
            past_key_values=outputs.past_key_values,
            hidden_states=outputs.hidden_states,
            attentions=outputs.attentions,
            history_mask=outputs.history_mask,
        )
    

    def generate_with_parallel(
        self,
        input_ids,
        attention_mask=None,
        use_cache=None,
        eos_token_id=None,
        para_begin_token_id=None,
        para_end_token_id=None,
        title_start_token=None,
        title_end_token=None,
        stage1_max_new_tokens=1024,
        stage2_max_new_tokens=4096,
        stage3_max_new_tokens=2048,
        sync_start_id=151669,
        sync_end_id=151670,
        return_parallel_info=False,
        **kwargs
    ):
        # ä»…æ”¯æŒ batch size = 1
        assert input_ids.size(0) == 1, "batchsize must be 1"

        # å¸¸é‡ä¸çŠ¶æ€åˆå§‹åŒ–
        para_stop_tokens = [
            title_start_token,
            title_end_token,
            eos_token_id,
            sync_start_id,
            sync_end_id,
            para_begin_token_id,
            para_end_token_id,
        ]
        para_spuress_tokens = [
            title_start_token,
            title_end_token,
            sync_start_id,
            sync_end_id,
            para_begin_token_id,
        ]

        left_tokens = stage1_max_new_tokens + stage2_max_new_tokens + stage3_max_new_tokens
        seq_time = 0.0
        para_time = 0.0
        para_infos = []
        past_key_values = None
        first_time = True

        # å·¥å…·å‡½æ•°
        def build_logits_processor(first_flag):
            return LogitsProcessorList(
                [
                    CustomLogitsProcessor(
                        eos_token_id=eos_token_id,
                        para_begin_token_id=para_begin_token_id,
                        para_end_token_id=para_end_token_id,
                        title_start_token=title_start_token,
                        title_end_token=title_end_token,
                        sync_start_id=sync_start_id,
                        sync_end_id=sync_end_id,
                        first_time=first_flag,
                    )
                ]
            )

        def run_generate_step(cur_input_ids, max_new_tokens, pkv, first_flag):
            return self.generate(
                input_ids=cur_input_ids,
                logits_processor=build_logits_processor(first_flag),
                eos_token_id=[eos_token_id, sync_start_id],
                pad_token_id=eos_token_id,
                max_new_tokens=max_new_tokens,
                past_key_values=pkv,
                return_dict_in_generate=True,
                **kwargs,
            )

        def append_sync_content(gen_sequences, prompt_len):
            stage_seq = gen_sequences[0][prompt_len:]
            para_infos.append({"type": "sync", "content": stage_seq.tolist()})
            return stage_seq

        def find_parallel_candidates(gen_sequences, prompt_len):
            """
            è¿”å›: num_para, each_beam (ç”¨äºå¹¶è¡Œç”Ÿæˆçš„å‰ç¼€), topics (å¹¶è¡Œä¸»é¢˜)
            """
            seq_list = gen_sequences[0].tolist()
            para_begin_pos = lindex(seq_list, title_start_token, prompt_len)
            if para_begin_pos <= -1:
                return 0, [], []

            para_end_pos = rindex(seq_list[para_begin_pos:], title_end_token) + para_begin_pos + 2
            para_sequence = seq_list[para_begin_pos:para_end_pos]

            beams = [b for b in split_list(para_sequence, title_start_token) if b]
            if not beams:
                return 0, [], []

            beams = [b[1:-1] for b in beams]  # å»æ‰å ä½å¤´å°¾
            beams[-1] = beams[-1][:-1]        # å»é™¤æœ«å°¾ </s
            beams = deduplicate_lists(beams)
            topics = beams[:]
            beams = [[para_begin_token_id] + b + [25, 220] for b in beams]  # æ„é€ æ¯ä¸ªbeamçš„å‰ç¼€
            return len(beams), beams, topics

        def do_parallel_generation(
            num_para, each_beam, pkv, para_begin_position, max_length
        ):
            """
            è°ƒç”¨å¹¶è¡Œç”Ÿæˆå¹¶è¿”å›:
            - stage2_sequences: åˆå¹¶åçš„æ‰€æœ‰å¹¶è¡Œ token
            - conclustion_ids: å¹¶è¡Œç»“æŸåçš„åŒæ­¥ token
            - generated_output_para_each_beam: æ¯ä¸ªbeamçš„ç”Ÿæˆç»“æœ(list of list)
            - new_pkv: å¹¶è¡Œç”Ÿæˆåçš„ç¼“å­˜
            """
            nonlocal para_time
            if num_para <= 0:
                return [], [], [], pkv

            start_t = time.time()
            generated_output_para, new_pkv, history_mask = self.generate_parallel(
                input_ids=None,
                attention_mask=None,
                use_cache=use_cache,
                bos_token_id=para_begin_token_id,
                para_end_token_id=para_end_token_id,
                eos_token_id=eos_token_id,
                max_length=max_length,
                para_begin_position=para_begin_position,
                num_para=num_para,
                past_key_values=pkv,
                each_beam_prefix=each_beam,
                para_stop_tokens=para_stop_tokens,
                para_spuress_tokens=para_spuress_tokens,
                **kwargs
            )
            para_time += time.time() - start_t

            # ä¾æ® history_mask åˆ‡åˆ†æ¯ä¸ªå¹¶è¡Œåˆ†æ”¯çš„ç»“æœ
            per_beam_outputs = [
                generated_output_para[0][(history_mask[i] - para_begin_position)]
                for i in range(num_para)
            ]
            per_beam_outputs_lists = [g.tolist() for g in per_beam_outputs]

            # åˆå¹¶æ‰€æœ‰å¹¶è¡Œå†…å®¹
            stage2_sequences = [token for beam in per_beam_outputs_lists for token in beam]
            conclustion_ids = [sync_end_id, 198]  # å›è½¦ç¬¦
            return stage2_sequences, conclustion_ids, per_beam_outputs_lists, new_pkv

        def early_return():
            if not return_parallel_info:
                return para_infos
            return {
                "seq_time": seq_time,
                "para_time": para_time,
                "para_infos": para_infos,
                "first_token_time": first_token_time,
            }

        # 1) é¦– token ç”Ÿæˆï¼ˆmax_new_tokens=1ï¼‰
        start_time = time.time()
        output_stage1 = run_generate_step(
            cur_input_ids=input_ids,
            max_new_tokens=1,
            pkv=past_key_values,
            first_flag=first_time,
        )
        first_token_time = time.time() - start_time

        generated_sequence = output_stage1.sequences
        past_key_values = output_stage1.past_key_values
        append_sync_content(generated_sequence, input_ids.shape[-1])

        # å°†å½“å‰ç”Ÿæˆæ‹¼å› input_ids ä»¥ç»§ç»­ç”Ÿæˆ
        input_ids = generated_sequence

        # 2) ä¸»å¾ªç¯
        while left_tokens > 0:
            start_t = time.time()
            output_stage = run_generate_step(
                cur_input_ids=input_ids,
                max_new_tokens=left_tokens,
                pkv=past_key_values,
                first_flag=first_time,
            )
            seq_time += time.time() - start_t
            first_time = False

            past_key_values = output_stage.past_key_values
            generated_sequence = output_stage.sequences

            # åŒæ­¥è¾“å‡ºè®°å½•ä¸å‰©ä½™tokenæ›´æ–°
            stage_new = append_sync_content(generated_sequence, input_ids.shape[-1])
            left_tokens -= stage_new.shape[-1]

            # è‹¥ä»¥ eos ç»“æŸï¼Œç›´æ¥è¿”å›
            if generated_sequence[0, -1] == eos_token_id:
                left_tokens = -1  # è§¦å‘é€€å‡ºé€»è¾‘
                return early_return()

            # è¯†åˆ«å¯å¹¶è¡Œç‰‡æ®µ
            num_para, each_beam, topics = find_parallel_candidates(
                generated_sequence, input_ids.shape[-1]
            )

            # å¹¶è¡Œç”Ÿæˆ
            stage2_input_ids = generated_sequence
            para_begin_position = generated_sequence.shape[-1] - 1

            if num_para > 0 and left_tokens > 0:
                stage2_sequences, conclustion_ids, each_beam_outputs, past_key_values = do_parallel_generation(
                    num_para=num_para,
                    each_beam=each_beam,
                    pkv=past_key_values,
                    para_begin_position=para_begin_position,
                    max_length=stage2_max_new_tokens,
                )
                left_tokens -= len(stage2_sequences)

                para_infos.append({
                    "type": "async",
                    "content": [{"title": topics[i], "content": out} for i, out in enumerate(each_beam_outputs)]
                })
                para_infos.append({"type": "sync", "content": conclustion_ids})
            else:
                # æ— å¹¶è¡Œæˆ–æ— å‰©ä½™ tokenï¼Œç›´æ¥è¿”å›
                return early_return()

            # æ‹¼æ¥å¹¶è¡Œå†…å®¹å›è¾“å…¥ï¼Œç»§ç»­å¾ªç¯
            merged_ids = stage2_input_ids[0].tolist() + stage2_sequences + conclustion_ids
            input_ids = torch.tensor([merged_ids], device=input_ids.device)

            if left_tokens <= 0:
                # å¯æ ¹æ®éœ€æ±‚å†³å®šæ˜¯å¦ç»§ç»­ï¼›è¿™é‡ŒæŒ‰åŸé€»è¾‘é‡ç½®ï¼Œç¡®ä¿å¾ªç¯ç»“æŸ
                left_tokens = 1024  # åŸä»£ç ä¸­çš„è¡Œä¸ºï¼ˆä½†æœªè¢«ä½¿ç”¨åˆ°è¿”å›è·¯å¾„ä¸Šï¼‰



    def generate_parallel(self,
        input_ids,
        attention_mask = None,
        use_cache = None,
        bos_token_id=None,
        eos_token_id = None,
        para_end_token_id = None,
        max_length = 100,
        para_begin_position=None,
        para_stop_tokens=None,
        para_spuress_tokens=None,
        num_para=1,
        past_key_values=None,
        each_beam_prefix:list[list]=None,
        do_sample=False, temperature=1.0, top_k=None, top_p=None,
        **kwargs
    ):
        with torch.no_grad():

            # ä½¿ç”¨bos_token_idåˆå§‹åŒ–ç”Ÿæˆçš„åºåˆ—
            generated_sequence = torch.tensor([[bos_token_id]*num_para], device=self.device,dtype=torch.long)

            beams_finished=None
            past_key_values=DynamicCache.from_legacy_cache(past_key_values)
            history_mask = {}
            activate_branch = list(range(num_para))
            history_mask['past_key_values_length_true'] = para_begin_position
            last_end_branch = []
            last_activate_branch = []
            previous_token = torch.tensor([[bos_token_id]*num_para], device=self.device,dtype=torch.long)
            seq_len = para_begin_position
            # å¼€å§‹ç”Ÿæˆåºåˆ—
            for i in range(max_length):
                seq_len += num_para
                # å½“å‰å¹¶è¡Œå—èµ·å§‹ä½ç½®
                current_start_idx = seq_len-num_para
                new_positions = torch.arange(
                    current_start_idx, current_start_idx + num_para, 
                    device=generated_sequence.device
                )
                for bi, branch_id in enumerate(activate_branch):
                    # è·å–è¯¥åˆ†æ”¯çš„å†å²ä½ç½®
                    branch_history = history_mask.get(branch_id, 
                        torch.tensor([], device=generated_sequence.device, dtype=torch.long))
                    # åˆ›å»ºè¯¥åˆ†æ”¯çš„å¯è§ä½ç½®æ©ç 
                    visible_positions = torch.cat([branch_history, new_positions[bi].unsqueeze(0)])
                    history_mask[branch_id] = visible_positions
                # seq_len += num_para
                # å‰å‘ä¼ æ’­
                outputs = self(
                    input_ids=generated_sequence[:, -num_para:].clone(),  # åªè·å–æœ€åä¸€ä¸ªè¯;å¹¶è¡ŒçŠ¶æ€ä¸‹ï¼Œé€‰æ‹©æœ€ånum_paraä¸ªtoken
                    attention_mask=None,
                    past_key_values=past_key_values,
                    use_cache=use_cache,
                    return_dict=True,
                    num_para=num_para,
                    history_mask=history_mask,
                    activate_branch=activate_branch,
                    para_begin_position=para_begin_position,
                    #beams_finished=beams_finished
                )
                history_mask['past_key_values_length_true']+=1
                logits = outputs.logits.detach()

                end_branch = []
                #å¯¹æœ€ånum_paraä¸ªtokenè¿›è¡Œé€‰æ‹©
                next_token=get_next_token_from_logits(logits,num_para=num_para,para_spuress_tokens=para_spuress_tokens)
                if each_beam_prefix is not None:
                    #æ¯ä¸ªbeamçš„å‰ç«¯å¼ºåˆ¶è®¾ä¸ºeach_beam_prefixä¸­å¯¹åº”ä½ç½®çš„å€¼
                    for idx,beam_id in enumerate(activate_branch):
                        if i<len(each_beam_prefix[beam_id])-1:
                            next_token[0,idx]=each_beam_prefix[beam_id][i+1]


                next_token[next_token == eos_token_id] = para_end_token_id # è®¾ç½®eosç­‰äºparaç»“æŸtoken
                for pre_idx, pre_branch in enumerate(last_activate_branch):
                    if pre_branch in activate_branch and previous_token[0][pre_idx] == para_end_token_id:
                        cur_idx = activate_branch.index(pre_branch)
                        next_token[0][cur_idx] = para_end_token_id
                        end_branch.append(pre_branch)
                


                activate_branch_tmp = activate_branch.copy()
                # æ ¹æ®ä¸Šä¸€æ¬¡è§£ç ç»“æœæ¥å†³å®šå»é™¤å“ªäº›åˆ†æ”¯
                if end_branch:
                    for j in end_branch:
                        activate_branch.remove(j)
                    num_para = len(activate_branch)
                    # å°†ç”Ÿæˆçš„è¯ï¼ˆæœªè¢«åˆ é™¤çš„åˆ†æ”¯tokenï¼‰æ·»åŠ åˆ°åºåˆ—ä¸­
                    if activate_branch:
                        branch2idx = [j for j in range(len(activate_branch_tmp)) 
                            if activate_branch_tmp[j] in activate_branch]
                        next_token = next_token[:, branch2idx]
                        generated_sequence = torch.cat([generated_sequence, next_token], dim=1)
                    else:
                        break
                else:
                    # å°†ç”Ÿæˆçš„è¯ï¼ˆnum_paraä¸ªï¼‰æ·»åŠ åˆ°åºåˆ—ä¸­
                    generated_sequence = torch.cat([generated_sequence, next_token], dim=1)

                # æ›´æ–° past_key_values
                past_key_values = outputs.past_key_values
                del outputs

                # å¦‚æœæ‰€æœ‰previous_tokençš„beaméƒ½å·²ç»ç”Ÿæˆç»“æŸæ ‡è®°ï¼Œåˆ™åœæ­¢ç”Ÿæˆ
                if isinstance(para_end_token_id, int):
                    if (previous_token == para_end_token_id).all():
                        break
                elif isinstance(para_end_token_id, list):
                    #å–å¹¶é›†
                    eos_mask_list=[]
                    for eos_token_id_i in para_end_token_id:
                        eos_mask_list.append(previous_token == eos_token_id_i)
                    from functools import reduce
                    result = reduce(torch.logical_or, eos_mask_list)
                    if result.all():
                        break
                
                # æš‚å­˜next_tokenå’Œæ´»è·ƒçš„åˆ†æ”¯
                previous_token=next_token
                last_activate_branch = activate_branch

        return generated_sequence,past_key_values,history_mask



def rindex(my_list, val):
    last_index = -1
    for i in range(len(my_list)-1, -1, -1):
        if my_list[i] == val:
            last_index = i
            break
    return last_index
def lindex(my_list, val, from_pos=None):
    last_index = -1
    if from_pos is None:
        for i in range(len(my_list)):
            if my_list[i] == val:
                last_index = i
                break
    else:
        for i in range(from_pos, len(my_list), 1):
            if my_list[i] == val:
                last_index = i
                break
    return last_index

def deduplicate_lists(*lists):
    """
    å¯¹å¤šä¸ªåˆ—è¡¨ä¸­çš„å­åˆ—è¡¨å»é‡ï¼Œè¿”å›æ‰€æœ‰å”¯ä¸€çš„å­åˆ—è¡¨ï¼ˆé¡ºåºä¿ç•™ç¬¬ä¸€æ¬¡å‡ºç°ï¼‰ã€‚
    æ¯ä¸ªå­åˆ—è¡¨å†…éƒ¨å…ƒç´ ä¸ºæ•´æ•°ã€‚
    """
    seen = set()
    result = []
    for lst in lists:
        for item in lst:
            t = tuple(item)  # åˆ—è¡¨è½¬å…ƒç»„ç”¨äºå“ˆå¸Œ
            if t not in seen:
                seen.add(t)
                result.append(item)
    return result
